import React from 'react'
import { Link } from 'react-router-dom'

function CartProduct({ id, image, title, price }) {

  const selectOption=()=>{
    for(let i = 0; i < 10; i++) {
      return (
        <option>i</option>
      )
    }
  }
  return (
    <div className="CartProduct">

        <div className="cartProduct__image">
          <Link to={`/product/${id}`}>
            <img src={image}  alt={title} />
          </Link>
        </div>
        <div className="cartProduct__otherDetail">
              <h4>{title}</h4>
              <h3>${price}</h3>
          <select name="" id="">{ selectOption()}</select>
              <button>Delete</button>
        </div>


    </div>
  )
}

export default CartProduct
