import { signInWithEmailAndPassword } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { signOut } from 'firebase/auth';
import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { auth, db } from './backend/firebase';
import { login } from './features/userSlice';
import { useDispatch } from 'react-redux';
import "./SignIn.css";


function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const handleSignIn = (eventObj) => {
    eventObj.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
    .then((userCredential) => {
      getDoc(doc(db, "Users", userCredential.user.uid))
        .then((doc) => {
        dispatch(login({
          fullName: doc.data().fullName,
          userId: doc.data().userId,
          email: doc.data().email,
          cart: doc.data().cart, //map the cart into the array
        }))
      })
    })
    .then(() => {
      navigate('/')
      setEmail("")
      setPassword("")
    })
    .catch(error => console.log(error))
  }
  const handleSignOut=() => {
    signOut(auth)
      .then((user) => {
      console.log(user, "signed Out")
    })
  }
  return (

    <div className='SignIn'>
      <div className="signIn__container">
        <h3>Log in </h3>
        <form onSubmit={handleSignIn} action="">
            <input value={email} onChange={(e)=>{setEmail(e.target.value)}} type="text" placeholder="Email"  required/>
            <input value={password} onChange={(e)=>{setPassword(e.target.value)}} type="password" placeholder="password" required />
            <button>Login</button>
            <p>Not a member <Link to="signUp">Create An Account</Link></p>
        </form>
      </div>

    </div>
  )
}

export default SignIn;
