import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import Product from './Product';
import './Home.css'
import { useDispatch, useSelector } from "react-redux";
import { auth, db } from './backend/firebase';
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  collection,
  addDoc,
  serverTimestamp,
  getDocs,
  query,
  orderBy,
  onSnapshot
} from 'firebase/firestore';
import {defaultCartState, firstCall, updateCart} from "./features/cartSlice"

function AllProduct({ products }) {
  const user = useSelector(state => state.user.user);
  const cart = useSelector(state => state.cart.cart);
  const dispatch = useDispatch();
  console.log(user)
  // console.log(cart)
  // const cartColRef = collection(db, "Users", user.userId, "userCart");
  // console.log(user.userId);
  // console.log(products);
  useEffect(() => {
    if (user) {
      const cartColRef = collection(db, "Users", user.userId, "userCart");
      const q = query(cartColRef, orderBy("timestamp", "desc"));
      onSnapshot(q,(snapshot) => {
        const cart=[];
        snapshot.docs.forEach((doc)=>{
          cart.push(doc.data())
        })
        // console.log(cart)
      dispatch(firstCall(cart));
      })
    }
    else {
      dispatch(defaultCartState())
    }
  },[user])


  const handleAddToCart = (thisProduct) => {
    const cartColRef = collection(db, "Users", user.userId, "userCart");
    const q = query(cartColRef, orderBy("timestamp", "desc"));
    // if(doc(db,"Users", user.userId,  ))

    console.log(thisProduct)

    // addDoc(cartColRef,
    setDoc(doc(db, "Users", user.userId, "userCart", thisProduct.id),
    {
    ...thisProduct,
    timestamp: serverTimestamp(),
      quantity: 1,
    })

    // .then(()=>{
    // onSnapshot(q,(snapshot) => {
    //     const cart=[];
    //     snapshot.docs.forEach((doc)=>{
    //       cart.push(doc.data())
    //     })
    //     // console.log(cart)
    //   dispatch(updateCart(cart));
    //   console.log(cart);
    //   })
    // })
    // .catch(()=>{
    //   console.log()
    // })
  }

  return (
    <div className="allProducts">
        {products.map((prod) => {
          return (
            <div className="products" key ={prod.id}>
                      <Product
                          id={prod.id}
                          title={prod.title}
                          price={prod.price}
                          image={prod.thumbnail}
                  />
              <button onClick={() => { handleAddToCart(prod) }}>Add to cart</button>
            </div>

          )

        })}
         </div>
  )
}

export default AllProduct;
