import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import CartProduct from './CartProduct';

function Cart() {
  const carts = useSelector(state => state.cart.cart);
  const [total, setTotal] = useState(0)
  const [quantity, setQuantity]= useState(0)

  const sumQunatity = (cart) => {
    const cartQautity = cart.map(product => product.quantity);
    const sumQuantity = cartQautity.reduce((x, y) => x + y);
    return sumQuantity;
  }
  const sumTotal = (cart) => {
    const prices = cart.map(product => product.productTotal);
    const priceTotal = prices.reduce((total, price) => total + price);
    return priceTotal;
  }
  useEffect(() => {
    if (carts.length) {
      setQuantity(sumQunatity(carts));
      setTotal(sumTotal(carts))
    }
  },[carts])
  return (
    <div>
      <h1>subTotal ${total}</h1>
      <h1>cart quantity {quantity}</h1>
      <button>Proceedd to checkout</button>

      {carts.map((product) => {
        return (
          <div className="product" key={product.id}>
            <CartProduct
              id={product.id}
              image={product.thumbnail}
              title={product.title}
              price={product.price}
            />
            <div className="cartProduct__otherDetail">
          <h4>{product.title}</h4>
          <h3>${product.price}</h3>
          <select name="" id="">
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="6">6</option>
            <option value="7">7</option>
            <option value="8">8</option>
            <option value="9">9</option>
            <option value="">10+</option>
          </select>
          <button>Delete</button>
        </div>
          </div>)

      })}
    </div>
  )
}

export default Cart
