import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    cart: []
};

export const cartSlice = createSlice({
    name: "cart",
    initialState,
    reducers: {
        firstCall: (state, action) => {
            state.cart = action.payload;
        },
        updateCart: (state, action) => {
            state.cart = action.payload;
        },
        lastCall: (state) => {
            state.cart = [];
        }

    }
})
