  const [total, setTotal] = useState(0)
  const [quantity, setQuantity]= useState(0)
  console.log(carts)
  useEffect(() => {
    setCart(carts)
  },[carts])
  return (
    <div>
      <h1>the cart is { carts.length}</h1>
      {carts.map((product) => {
        return (<div className="product" key={product.id}>
          <CartProduct
          id={product.id}
          image={product.thumbnail}
