import React, { useEffect, useRef, useState } from 'react'
import HomeIcon from '@mui/icons-material/Home';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';
import ContactPhoneIcon from '@mui/icons-material/ContactPhone';
import GroupsIcon from '@mui/icons-material/Groups';
import MenuIcon from '@mui/icons-material/Menu';
import "./NavBar.css"
import { Link } from 'react-router-dom';
const navList = [
    {
        title: 'Home',
        path: '/',
        icon: <HomeIcon className='menu__list__icon'/>,
        cName:'nav__text'
    },
    {
        title: 'About Us',
        path: '/about',
        icon: <GroupsIcon className='menu__list__icon'/>,
        cName:'nav__text'
    },
    {
        title: 'Contact Us',
        path: '/contact',
        icon: <ContactPhoneIcon className='menu__list__icon'/>,
        cName:'nav__text'
    }
] 
function NavBar() {
  const [sidebar, setSideBar] = useState(false);
  const menuRef = useRef()
  useEffect(() => {
    console.log(sidebar);
    document.onmousedown = (eventObj) => {
      if (!menuRef.current.contains(eventObj.target)) {
        setSideBar(false);
      }

    }
  });
  const handleOpenMenu = () => {
    setSideBar(!sidebar); // this toggles the state 
  }
  return (
    <div ref={menuRef} className="nav__container ">
      <MenuIcon onClick={handleOpenMenu} className='nav_menuOpen' />
      <nav className={!sidebar? 'navBar': "navBar active"}>
        <div className="menu__content">
          {navList.map((item, index) => {
            return (
              <div key={index} className={item.cName}>
                    <Link to={item.path} onClick={handleOpenMenu}>
                      <span>{item.title}</span>
                      {item.icon}



                    </Link>

                  </div>
            )
          })}
        </div>
      </nav>
    </div>

  )
}

export default NavBar;
