import { signInWithEmailAndPassword } from 'firebase/auth';
import { doc, getDocs } from 'firebase/firestore';
import { signOut } from 'firebase/auth';
import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { auth, db } from './backend/firebase';

function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const handleSignIn = (eventObj) => {
    eventObj.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
    .then((userCredential) => {
      console.log("user signed in", userCredential.user.uid)
      getDocs(doc(db, "Users", userCredential.user.uid))
      .then((doc) => {
        console.log(doc.id, doc.data())
      })

    })
  }
  const handleSignOut=() => {
    signOut(auth)
      .then((user) => {
      console.log(user, "signed Out")
    })
  }
  return (
    <div className='SignIp'>
          <h3>Log in </h3>
          <form onSubmit={handleSignIn} action="">
              <input value={email} onChange={(e)=>{setEmail(e.target.value)}} type="text" placeholder="Email"  required/>
              <input value={password} onChange={(e)=>{setPassword(e.target.value)}} type="password" placeholder="password" required />
              <button>Login</button>
              <p>Not a member <Link to="signUp">Create An Account</Link></p>
          </form>
          <button onClick={handleSignOut} >signout</button>
    </div>
  )
}

export default SignIn;
