import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import Product from './Product';
import './Home.css'
import { useDispatch, useSelector } from "react-redux";
import { auth, db } from './backend/firebase';
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  collection,
  addDoc,
  serverTimestamp,
  getDocs,
  query,
  orderBy,
  onSnapshot,
  increment
} from 'firebase/firestore';
import { defaultCartState, firstCall, updateCart } from "./features/cartSlice"
import handleAddToCart from './handleAddToCart';

function AllProduct({ products }) {
  const user = useSelector(state => state.user.user);
  const cart = useSelector(state => state.cart.cart);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  // console.log(user)
  // console.log(cart)
  // const cartColRef = collection(db, "Users", user.userId, "userCart");
  // console.log(user.userId);
  // console.log(products);
  useEffect(() => {
    if (user) {
      const cartColRef = collection(db, "Users", user.userId, "userCart");
      const q = query(cartColRef, orderBy("timestamp", "desc"));
      onSnapshot(q,(snapshot) => {
        const cart=[];
        snapshot.docs.forEach((doc)=>{
          cart.push(doc.data())
        })
        // console.log(cart)
      dispatch(firstCall(cart));

      })
    }
    else {
      dispatch(defaultCartState())
    }
  },[user])




  return (
    <div className="allProducts">
      {products.slice(0,4).map((prod) => {
        return (
          <div className="products" key ={prod.id}>
                    <Product
                        id={prod.id}
                        title={prod.title}
                        price={prod.price}
                        image={prod.thumbnail}
                        rating={prod.rating}
                />
            <button onClick={() => { handleAddToCart(prod, user, dispatch, navigate) }}>Add to cart</button>
          </div>
        )
      })}
      <img src="https://images.unsplash.com/photo-1519389950473-47ba0277781c?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80" alt="" />
    </div>
  )
}

export default AllProduct;
