// import { Counter } from './features/counter/Counter';
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from 'react-router-dom';
import Nav from './Nav';
import Home from './Home';
import About from './About';
import Contact from './Contact';
import Cart from './Cart';
import ProductDetail from './ProductDetail';
import NavBar from './NavBar';
import Header from './Header';
import { useEffect, useState } from 'react';
import useFetchProduct from './useFetchProduct';

function App() {
  const [searchProducts, setSearchProducts] = useState()
  // useEffect(() => {
  //   console.log(searchProducts)
  //   // searchProducts  === "" && setSearchProducts(null)
  // }, [searchProducts])
  // const getSearchKey=(searchKey)=>{
  //   setSearchKey(searchKey)
  // }
  return (
    <Router>
      <div className="App">
        {/* <Nav /> */}
        <Header  setSearch ={setSearchProducts} />

        <div className="app-content">
          <Routes> 
            <Route path="/" element={
              <Home searchProducts={searchProducts} />}
                />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/product/:id" element={<ProductDetail/>}/> {/*:id can be used to access a particular id of a product */}
            <Route path="/cart" element={<Cart />} />

          </Routes>

        </div>
    </div>
    </Router>
  );
}

export default App;
