import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import Product from './Product';
import './Home.css'
import { useSelector } from "react-redux";
import { auth, db } from './backend/firebase';
import {doc, getDoc, setDoc, updateDoc, collection, addDoc, serverTimestamp} from 'firebase/firestore'

function AllProduct({ products }) {
  const user = useSelector(state => state.user.user)
  const cartColRef = collection(db, "Users", user.userId, "userCart");
  // console.log(user.userId);
  // console.log(products);


  const handleAddToCart = (thisProduct) => {
    // updateDoc(doc(db, "Users", user.userId), {
    //   cart: this.cart
    // })
    // .then((doc) => {
    //     console.log(doc.data())
    // })
    // .catch((err) => console.log(err))
    // console.log(thisProduct)
    addDoc(cartColRef, thisProduct)
    .then(() => {
      updateDoc(cartColRef, {
        timeStamp: serverTimestamp(),
      })
    })
    .catch(()=>{
      console.log()
    })
  }

  return (
    <div className="allProducts">
        {products.map((prod) => {
          return (
            <div className="products" key ={prod.id}>
                      <Product
                          id={prod.id}
                          title={prod.title}
                          price={prod.price}
                          image={prod.thumbnail}
                  />
              <button onClick={() => { handleAddToCart(prod) }}>Add to cart</button>
            </div>

          )

        })}
         </div>
  )
}

export default AllProduct;
